protected function MakeTicketRef($iNextId)
{
        switch(get_class($this))
        {
                case 'UserRequest':
                $sFormat = 'R-%06d';
                break;

                case 'Incident':
                $sFormat = 'I-%06d';
                break;

                case 'Change':
                case 'RoutineChange':
                case 'EmergencyChange':
                case 'NormalChange':
                $sFormat = 'C-%06d';
                break;

                case 'Problem':
                $sFormat = 'P-%06d';
                break;

                case 'Observation':
                $sFormat = 'O-%06d';
                break;

                case 'Access':
                $sFormat = 'A-%06d';
                break;

                case 'Evento':
                $sFormat = 'E-%06d';
                break;

                default:
                $sFormat = 'T-%06d';
        }
        return sprintf($sFormat, $iNextId);
}

