							'ticket' => $this->GetKey(),
							)
						);
		while($oEvento = $oChildEventoSet->Fetch())
		{
			$oEvento->set('public_log',$sLogPublic);
			$oEvento->DBUpdate();
		}

	}
	$oLog = $this->Get('private_log');
	$sLogPrivate = $oLog->GetModifiedEntry('html');
	if ($sLogPrivate != '')
